import React, { useState } from 'react';
import './SearchComponent.css';

const SearchComponent = () => {
    const [searchTerm, setSearchTerm] = useState(""); // Sử dụng useState hook để lưu giá trị của trường input

    const handleInputChange = (e) => {
        setSearchTerm(e.target.value); // Cập nhật giá trị của trường input khi người dùng nhập
    };

    return (
        <div className="main-search-input-wrap">
            <div className="main-search-input fl-wrap">
                <div className="main-search-input-item">
                    <input
                        type="text"
                        value={searchTerm} // Sử dụng giá trị từ biến trạng thái
                        onChange={handleInputChange} // Gọi hàm khi giá trị thay đổi
                        placeholder="Search by title job..."
                    />
                </div>
                <button className="main-search-button">Search</button>
            </div>
        </div>
    );
};

export default SearchComponent;
