import React, {useState} from 'react';
import axios from 'axios';
import ReactLoading from 'react-loading';

const SearchComponent = ({onSearch}) => {
    const [city, setCity] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    function updateCity(event) {
        setCity(event.target.value);
    }

    function submitSearch(event) {
        setIsLoading(true);
        event.preventDefault();
        if (city.length > 0) {
            let url = `https://devfinding.com/api/v1/jobs/search?query=${encodeURIComponent(city)}`;

            axios
                .get(url)
                .then((response) => {
                    onSearch(response.data);
                })
                .catch((error) => {
                    alert('Error fetching data. Please try again later.');
                    console.error(error);
                })
                .finally(() => {
                    setIsLoading(false);
                });
        } else {
            alert('Please type a city');
            setIsLoading(false);
        }
    }

    return (
        <div className="main-search-input-wrap">
            <div className="main-search-input fl-wrap">
                <div className="main-search-input-item">
                    <input
                        type="search"
                        placeholder="Type keyword here..."
                        value={city}
                        onChange={updateCity}
                    />
                </div>
                <button className="main-search-button" onClick={submitSearch}>
                    Search
                </button>
            </div>
            {isLoading && <ReactLoading type="balls" color="red" height="100px" width="100px"/>}
        </div>
    );
};

export default SearchComponent;
